    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        
        try{
                               
            int indice = Busqueda(ingresaArray.getText(), busqueda.getText());
            
            if (indice != -1)
            {
                txtLugarElemento.setText(indice+"");
            }
            else
            {
                txtLugarElemento.setText("No se encuentra en el arreglo");
            }
        }
        catch(NumberFormatException e)
        {
            JOptionPane.showMessageDialog(null,"Solo debe ingresar valores enteros");
        }
    }//GEN-LAST:event_btnBuscarActionPerformed


